import { useState } from "react";
import { useNavigate } from "react-router-dom";

function Shop() {
  const navigate = useNavigate();
  const [items] = useState(["Phone", "Laptop", "Headphones"]);
  const [cart, setCart] = useState([]);

  const add = (item) => {
    setCart([...cart, item]);
  };

  const checkout = async () => {
    const token = localStorage.getItem("token");

    await fetch("https://rrr-shopkart-backend.onrender.com/api/save-order/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        items: cart.join(", "),
        total: cart.length * 1000,
      }),
    });

    navigate("/success");
  };

  return (
    <div className="page">
      <h2>Shop</h2>

      {items.map((i) => (
        <button key={i} onClick={() => add(i)}>
          Add {i}
        </button>
      ))}

      <h3>Cart: {cart.join(", ")}</h3>

      <button onClick={checkout}>Checkout</button>
    </div>
  );
}

export default Shop;
