  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“¦ Place Order</h2>

      {cart.map((item, index) => (
        <p key={index}>
          {item.name} - â‚¹{item.price}
        </p>
      ))}

      <h3>Total: â‚¹{total}</h3>

      <button
        style={{ padding: "10px", marginTop: "20px" }}
        onClick={() => navigate("/success")}
      >
        Confirm Order
      </button>
    </div>
  );
}

export default Order;
